mapper.select:
SELECT * FROM exam_answers 
WHERE exam_id = #{examId} AND user_id = #{userId}
ORDER BY question_id

SELECT * FROM exam_answers 
WHERE exam_id = #{examId} AND user_id = #{userId} AND question_id = #{questionId}
LIMIT 1

mapper.update:
UPDATE exam_answers
SET answer = #{answer},
    score = #{score},
    submit_time = #{submitTime}
WHERE id = #{id}
